$("document").ready(function(){$(".show-more").on("click",function(t){t.preventDefault(),$.ajax({context:this,type:"GET",url:"php/expandmessage.php",data:{id:$(this).attr("data-id")},success:function(t){$(this).parent()[0].innerHTML=JSON.parse(t)[0]}})}),$(".likeBtn").on("click",function(t){t.preventDefault(),$.ajax({context:this,type:"POST",url:"php/givelike.php",data:{id:$(this).attr("data-id")},success:function(t){$(this).html('<i class="fa fa-thumbs-up">'+t)}})})});